mapper.update:
update single_user
set user_nickname = #{userNickname},
    user_password = #{userPassword},
    user_phone = #{userPhone},
    user_profile = #{userProfile}
where user_id = #{userId}

 	update single_user
	set user_nickname = #{userNickname},
	    user_password = #{newpass},
	    user_phone = #{userPhone},
	     user_profile = #{userProfile}
	where user_id = #{userId}

mapper.select:
select g.group_num groupNum , m.user_id as userId , g.group_name as groupName , g.group_description as groupDescription , g.group_img_src as groupImgSrc , g.group_member_count as groupMemberCount , g.group_member_limit as groupMemberLimit , g.group_main_place as groupMainPlace , to_char(g.group_createdate, 'yyyy/mm/dd') as groupCreatedate from single_group g, single_group_member m where g.group_num = m.group_num and g.group_statecode = '1' and m.user_id = #{userId}
and m.group_member_statecode = 'L'
and m.group_member_statecode = 'M'

mapper.delete:
	delete from single_stamp
   where user_id = #{userId}
   	and group_num = #{groupNum}
